sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
isDateToday: function(zedd){
	var today = new GlideDateTime().getLocalDate();
	var needdate = new GlideDateTime(zedd).getLocalDate();
	if(needdate.compareTo(today) == 0){
			return true;
		}else{
			return false;
		}
	},
    type: 'NeedItUtils'
};
